import React, { useState } from 'react';
import { Flex } from '../components';
import { Button } from '../components';
import DigitButton from '../components/@commons/Button/DigitButton/DigitButton';
import LottoButton from '../components/@commons/Button/LottoButton/LottoButton';
import LottoGeneratorContainer from '../components/@commons/Button/LottoButton/LottoGenerator';

const Lotto = () => {
  const [isClicked, setIsClicked] = useState(false);

  const handleClickLottoButton = () => {
    setIsClicked(!isClicked);
  };

  return (
    <>
      <Flex>
        {!isClicked ? (
          <LottoButton title={'로또 번호 생성기'} onClickLottoButton={handleClickLottoButton}></LottoButton>
        ) : (
          <Flex flexDirection='column'>
            <div>dddd</div>
            <LottoButton title={'처음으로 돌아가기'} onClickLottoButton={handleClickLottoButton}></LottoButton>
          </Flex>
        )}
      </Flex>
    </>
  );
};

export default Lotto;
