      if (currentMove === 'Î∞îÏúÑ') {
        result = 'ÎπÑÍ≤ºÏäµÎãàÎã§.';
      } else if (currentMove === 'Í∞ÄÏúÑ') {
        result = 'Ïª¥Ìì®ÌÑ∞ ÏäπÎ¶¨';
      } else if (currentMove === 'Î≥¥') {
        result = 'Ïú†Ï†Ä ÏäπÎ¶¨';
      }
    } else if (computerMove === 'Î≥¥') {
      if (currentMove === 'Î∞îÏúÑ') {
        result = 'Ïª¥Ìì®ÌÑ∞ ÏäπÎ¶¨';
      } else if (currentMove === 'Í∞ÄÏúÑ') {
        result = 'Ïú†Ï†Ä ÏäπÎ¶¨';
      } else if (currentMove === 'Î≥¥') {
        result = 'ÎπÑÍ≤ºÏäµÎãàÎã§';
      }
    }

    handleState({
      ...state,
      gameResult: result,
    });
  };

  const onClickHandButton = (e) => {
    handleState({
      ...state,
      currentMove: e.target.textContent,
    });
  };

  const handlePrintRandomHand = () => {
    const array = ['‚úåÔ∏è', '‚úäüèª', 'üñêüèø'];

    let index = 0;

    function count(start, end) {
      let index = 0;
      while (start < end) {
        console.log();
      }
    }

    console.log(array[index]);

    if (index === array.length) index = 0;
  };

  const useInterval = (callback, delay, option) => {
    const savedCallback = useRef(); // ÏµúÍ∑ºÏóê Îì§Ïñ¥Ïò® callbackÏùÑ Ï†ÄÏû•Ìï† refÎ•º ÌïòÎÇò ÎßåÎì†Îã§.

    useEffect(() => {
      savedCallback.current = callback; // callbackÏù¥ Î∞îÎÄî ÎïåÎßàÎã§ refÎ•º ÏóÖÎç∞Ïù¥Ìä∏ Ìï¥Ï§ÄÎã§.
    }, [callback]);

    useEffect(() => {
      const tick = () => {
        savedCallback.current(); // tickÏù¥ Ïã§ÌñâÎêòÎ©¥ callback Ìï®ÏàòÎ•º Ïã§ÌñâÏãúÌÇ®Îã§.
      };
      if (delay !== null) {
        // ÎßåÏïΩ delayÍ∞Ä nullÏù¥ ÏïÑÎãàÎùºÎ©¥
        let id = setInterval(tick, delay); // delayÏóê ÎßûÏ∂îÏñ¥ intervalÏùÑ ÏÉàÎ°ú Ïã§ÌñâÏãúÌÇ®Îã§.
        return () => clearInterval(id); // unmountÎê† Îïå clearIntervalÏùÑ Ìï¥Ï§ÄÎã§.
      }
    }, [option, delay]); // delayÍ∞Ä Î∞îÎÄî ÎïåÎßàÎã§ ÏÉàÎ°ú Ïã§ÌñâÎêúÎã§.
  };

  const createRPSButtons = () => {
    const movementArray = ['Í∞ÄÏúÑ', 'Î∞îÏúÑ', 'Î≥¥'];

    return movementArray.map((el, i) => {
      return <Button type='submit' key={i} title={el} onClickEvent={onClickHandButton} />;
    });
  };

  return (
    <Form ref={form} onSubmit={(e) => e.preventDefault()}>
      <P title={printCurrentMove()} />
      {createRPSButtons()}
      <P title={printCurrentMove()} style={{ fontSize: 80, margin: 10, padding: 0 }} />
      {useInterval(() => handlePrintRandomHand(), 1000, currentMove)}
