import React, { useEffect, useMemo, useRef, useState } from 'react';
import { createRandomNumbers } from '../../utils/mathUtils';
import { Flex } from '../@commons/Flex/Flex';
import { P } from '../@commons/P/P';
import LottoButton from '../@commons/Button/LottoButton/LottoButton';

const LottoGeneratorContainer = (props) => {
  const { onClickGoBackButton } = props;

  const lottoNumbers = useMemo(() => Array.from(createRandomNumbers(7, 1, 45)), []);

  // when button is clicked 7 lottoNumbers need to be rendered on the page
  const [winNumbers, setWinNumbers] = useState(lottoNumbers);
  const [winBalls, setWinBalls] = useState([]);
  const [bonus, setBonus] = useState(null);
  const lottoNumbersArray = useRef([]);

  useEffect(() => {
    for (let i = 0; i < winNumbers.length - 1; i++) {
      lottoNumbersArray.current[i] = setTimeout(() => {
        setWinBalls((prevBalls) => [...prevBalls, Number(winNumbers[i])]);
      }, (i + 1) * 1000);
    }
    lottoNumbersArray.current[6] = setTimeout(() => {
      setBonus(Number(winNumbers[6]));
    }, 7000);
    return () => {
      lottoNumbersArray.current.forEach((v) => {
        clearTimeout(v);
      });
    };
  }, [lottoNumbersArray.current]);

  const createLottoElement = () => {
    return winBalls.map((el, i) => <P key={i} title={el}></P>);
  };

  return (
    <Flex flexDirection='column'>
      <div>
        <p>로또 당첨번호</p>
        {createLottoElement()}
        {console.log(lottoNumbersArray.current)}
      </div>
      <LottoButton title={'처음으로 돌아가기'} onClickButton={onClickGoBackButton} style={{ color: 'brown' }}></LottoButton>
    </Flex>
  );
};

export default LottoGeneratorContainer;
