import React, { useEffect, useMemo, useRef, useState } from 'react';
import { createRandomNumbers } from '../../utils/mathUtils';
import { Flex } from '../@commons/Flex/Flex';
import { P } from '../@commons/P/P';
import LottoButton from '../@commons/Button/LottoButton/LottoButton';

const LottoGeneratorContainer = (props) => {
  const { onClickGoBackButton } = props;

  const createLottoNumbers = useMemo(() => Array.from(createRandomNumbers(7, 1, 45)), []);

  // when button is clicked 7 lottoNumbers need to be rendered on the page
  const [lottoNumbers, setLottoNumbers] = useState(createLottoNumbers.slice(1, createLottoNumbers.length));
  const [winningNumbers, setWinningNumbers] = useState([]);
  const [bonusNumber, setBonus] = useState(createLottoNumbers[createLottoNumbers.length - 1]);
  const [numbers, setNumbers] = useState({
    lottoNumbers: createLottoNumbers.slice(1, createLottoNumbers.length),
    winningNumbers: [],
    bonusNumber: createLottoNumbers[createLottoNumbers.length - 1],
  });
  const timeOuts = useRef([]);

  useEffect(() => {
    const { lottoNumbers, winningNumbers, bonusNumber } = numbers;

    for (let i = 0; i < lottoNumbers.length; i++) {
      timeOuts.current[i] = setTimeout(() => {
        // setWinningNumbers((prev) => [...prev, Number(lottoNumbers[i])]);
        setNumbers({
          ...numbers,
          winningNumbers: [...winningNumbers, Number(lottoNumbers[i])],
        });
      }, (i + 1) * 1000);
    }
    timeOuts.current[6] = setTimeout(() => {
      setNumbers({
        ...numbers,
        bonusNumber: Number(bonusNumber),
      });
      // setBonus(Number(lottoNumbers[6]));
    }, 7000);
    return () => {
      timeOuts.current.forEach((item) => clearTimeout(item));
    };
  }, [timeOuts.current]);

  const createLottoElement = () => {
    return winningNumbers.map((el, i) => <P key={i} title={el}></P>);
  };

  return (
    <Flex flexDirection='column'>
      <div>
        <p>로또 당첨번호</p>
        {createLottoElement()}
        {console.log(timeOuts.current)}
      </div>
      <LottoButton title={'처음으로 돌아가기'} onClickButton={onClickGoBackButton} style={{ color: 'brown' }}></LottoButton>
    </Flex>
  );
};

export default LottoGeneratorContainer;
