import React, { useRef } from 'react';
import { Form } from '../@commons/Form/Form';
import { Button } from '../@commons/Button/Button';

const RcpDisplayContainer = (props) => {
  const { currentMove, handleCurrentMove } = props;

  const form = useRef();

  const updateCurrentMove = (e) => {
    console.log(e.target);
    handleCurrentMove({
      currentMove: e.target.textContent,
    });
  };

  return (
    <Form ref={form} onSubmit={(e) => e.preventDefault()}>
      <p>가위바위보 그림</p>
      <Button type='submit' title={'가위'} onClickEvent={updateCurrentMove} />
      <Button type='submit' title={'바위'} onClickEvent={updateCurrentMove} />
      <Button type='submit' title={'보'} onClickEvent={updateCurrentMove} />
      <p>나의 선택</p>
    </Form>
  );
};

export default RcpDisplayContainer;
