import React, { useState } from 'react';
import { Flex } from '../@commons/Flex/Flex';
import RcpDisplayContainer from './RcpDisplayContainer';
import RcpResultContainer from './RcpResultContainer';

const RpcGame = () => {
  // 상태로 관리해야 할 것들.
  const [state, setState] = useState({ currentMove: '가위' });

  const { currentMove } = state;

  return (
    <>
      <Flex flexDirection='column'>
        <RcpDisplayContainer currentMove={currentMove} handleCurrentMove={setState} />
        <RcpResultContainer />
        {console.log(currentMove)}
      </Flex>
    </>
  );
};

export default RpcGame;
