import React, { useRef } from 'react';
import { Form } from '../@commons/Form/Form';
import { Button } from '../@commons/Button/Button';
import { P } from '../@commons/P/P';

const RcpDisplayContainer = (props) => {
  const { currentMove, handleCurrentMove } = props;

  const form = useRef();

  const updateCurrentMove = (e) => {
    handleCurrentMove({
      currentMove: e.target.textContent,
    });
  };

  const printCurrentMove = () => {
    if (currentMove === 'Í∞ÄÏúÑ') {
      return '‚úåÔ∏è';
    } else if (currentMove === 'Î∞îÏúÑ') {
      return '‚úäüèª';
    } else if (currentMove === 'Î≥¥') {
      return 'üñêüèø';
    }
  };

  const styleObj = {
    fontSize: 50,
    margin: '0 auto',
    padding: 0,
  };

  return (
    <Form ref={form} onSubmit={(e) => e.preventDefault()}>
      <P title={printCurrentMove()} style={{ fontSize: 50 }} />
      <Button type='submit' title={'Í∞ÄÏúÑ'} onClickEvent={updateCurrentMove} />
      <Button type='submit' title={'Î∞îÏúÑ'} onClickEvent={updateCurrentMove} />
      <Button type='submit' title={'Î≥¥'} onClickEvent={updateCurrentMove} />
      <P title={printCurrentMove()} />
    </Form>
  );
};

export default RcpDisplayContainer;
